import React, { useEffect, useState } from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { PieChart } from 'react-minimal-pie-chart';
import axios from 'axios';

import '../css/Awards.css';

import { } from '@fortawesome/free-solid-svg-icons'

import { awards } from '../EditMe';

function Awards() {

    return (
        <Container className="awards-section">
            <Row>
                <Col xs={12} sm={6}>
                    <PieChart
                    data={awards.awardsData}
                    label={({ dataEntry }) => dataEntry.title}
                    labelStyle={(index) => ({
                        fill: awards.awardsData[index].color,
                        fontSize: awards.fontSize,
                    })}
                    radius={awards.radius}
                    labelPosition={awards.labelPosition}
                    lineWidth={awards.lineWidth}
                    paddingAngle={awards.paddingAngle}
                    />
                </Col>
                <Col xs={12} sm={6} className="awards-panel">
                    {awards.awardsData.map((skill) =>
                        <>
                            <p><span className="awards-highlight">{skill.title}</span><br />- {skill.description}<br />Experience: <span className="awards-highlight">{skill.experience}</span></p>
                        </>
                    )}
                </Col>
            </Row>
        </Container>
    );
}

export default Awards;
